#! /usr/local/bin/python
''' tested with Python 3.7.0 '''

def longest_palindrome_substring(s):
    ''' brute force algorithm'''

    n = len(s)
    max_len = 0
    palindrome = ''

    for j in reversed(range(n)):
        for i in range(n - j):
            x = s[i: i + j + 1]

            xLen = len(x)

            if (xLen < max_len):
                return palindrome

            for k in range(xLen // 2):
                if (x[k] != x[xLen - 1 - k]):
                    break
            else:
                if len(x) >= max_len:
                    max_len = len(x)
                    palindrome = x

    return palindrome


s = "pttwekewxyqyx"
print(longest_palindrome_substring(s))

s = "cbbd"
print(longest_palindrome_substring(s))

s = "babad"
print(longest_palindrome_substring(s))

s = "a"
print(longest_palindrome_substring(s))

s = ""
